// functional component that lists all the current comments related to the page 
import React, { useEffect, useInsertionEffect, useState } from 'react';
import CommentServices from '../services/CommentServices';
import {Link} from 'react-router-dom';

const ListCommentComponent = () => {
    
    const [comments, setComments] = useState([])

    useEffect(()=>{

        CommentServices.getAllComments().then(
            (response) => {
                setComments(response.data);
                console.log(response);
            }).catch(error => {
                console.log(error);
            })
    },[])

    return (
        <div className='container'>
            <h2 className='text-center'> List Comments </h2>
            <Link to="/add-comment" className='btn btn-primary mb-2'>Add Comment</Link>
            <table>
                <tbody>
                    {
                        comments.map(
                        comment =>
                        <div class="col-10">
                            <div class="d-flex">
                                <p><strong>{comment.userId}</strong></p>
                                <p class="mx-2 color-me">{comment.date}</p>
                            </div> 
                            <p> {comment.content}</p>
                            <div class="col d-flex col-md-6  col-xl-4 mb-md-3 justify-content-around align-items-center">
                                <p class="mb-0">Reply</p>
                                <p class="mb-0">Report</p>
                            </div>
        
                        </div>
                    )}
                </tbody>
            </table>
        </div>
    );
}

export default ListCommentComponent;
