import React,{useState,preventDefault} from 'react';

function AddCommentComponent(props) {
    
    const [commentContent,setCommentContents] = useState("")
    const [userid, setUserid] = useState("")
    const saveComment = (e) => {
        e.preventDefault();
        const comment = {userid,commentContent}
        console.log(JSON.stringify(comment))
    }

    return (
        <div>
            <div className='container'>
                <div className='row'>
                    <div className='card offset-md offset-md'>
                        <div className='card-body'>
                        <h5 class="card-title">Add your comment</h5>
                            <form>
                                <div className='form-group'>
                                    <p className='cardtext'>
                                        userid 
                                    </p>
                                    <input 
                                    type = "text"
                                    placeholder='Enter a userid'
                                    name = "userid"
                                    className='form-control-sm'
                                    value={userid}
                                    onChange = {(e) => setUserid(e.target.value)}
                                    ></input>
                                    </div>
                                    <div className='form-group'>
                                    <p className='form-label'>
                                        Comment Contents 
                                    </p>
                                    <textarea
                                    type = "text"
                                    placeholder='Enter your comments'
                                    name = "commentContent"
                                    className='form-control'
                                    value={commentContent}
                                    onChange = {(e) => setCommentContents(e.target.value)}
                                    ></textarea>
                                </div> 
                                <button className='btn btn-primary' onClick={(e)=>saveComment(e)}>comment</button>
                            </form>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default AddCommentComponent;
