// functional component that lists all the current comments related to the page 
import userEvent from '@testing-library/user-event';
import React, { useState } from 'react';

function ListCommentComponent(props) {
    
    const [comments, setComments] = useState([])

    return (
        <div className='container'>
            <h2 className='text-center'> List Comments </h2>
            <table className='table table-bordered table-striped'>
                <thead>
                    <th> User Id</th>
                    <th> Comment Date</th>
                    <th> Comment Content</th>
                    <th> Comment Upvote</th>
                </thead>
                <tbody>
                    comments.map(
                        comment => 
                        <tr key = {comment.id}>
                            <td> {comment.userId}</td>
                            <td> {comment.date}</td>
                            <td> {comment.content}</td>
                            <td> {comment.upvote}</td>
                        </tr>
                    )
                </tbody>
            </table>
        </div>
    );
}

export default ListCommentComponent;
