import axios from "axios";

const GETCOMMENTS_BASE_REST_API_URL = "http://localhost:3000/getComments";

class CommentService{
    getAllComments(){
        return axios.get(GETCOMMENTS_BASE_REST_API_URL)
    }
} 

export default new CommentService();
