import React,{Component} from 'react';
import CommentServices from '../services/CommentServices';
import {Link,useNavigate} from 'react-router-dom';


function AddCommentComponent (props) {
    // constructor(props){
    //     super(props)

        this.state = {
            commentContent:"",
            userid:""
        }
    //     this.changeCommentContentHandler = this.changeCommentContentHandler.bind(this);
    //     this.changeUseridHandler = this.changeUseridHandler.bind(this);
    //     this.saveComment = this.saveComment.bind(this)
    // }

        

        let comment = {userid:this.state.userid, commentContent:this.state.commentContent};
        console.log("comment =>" + JSON.stringify(comment));

        CommentServices.addComments(comment);
        // let navigate = useNavigate();
        const navigate = useNavigate();
        navigate("/", { replace: true }); 
    
    // changeCommentContentHandler = (event) => {
    //     this.setState({commentContent:event.target.value})
    // }
    // changeUseridHandler = (event) => {
    //     this.setState({userid:event.target.value})
    // }


        return (
            <div>
                <div className='container'>
                        <div className='card mb-3'>
                            <div className='card-body mb-3'>
                            <h5 className="card-title mb-3">Add your comment</h5>
                                <form>
                                    <div className='form-group'>
                                        <div className="input-group mb-3">
                                            <div className="input-group-prepend">
                                                <span className="input-group-text" id="basic-addon1">@</span>
                                            </div>
                                            <input type="text" class="form-control" placeholder="Userid" name = "userid" value={this.state.userid} aria-label="Username" aria-describedby="basic-addon1" ></input>
                                        </div>
                                    </div>

                                    <div className='form-group'>
                                        <textarea
                                            type = "text"
                                            placeholder='Enter your comments'
                                            name = "commentContent"
                                            className='form-control'
                                            value={this.state.commentContent}
                                            // onChange = {this.changeCommentContentHandler}
                                        ></textarea>
                                    </div> 
                                    <div className='mt-3'>
                                        <Link to="/" className='btn btn-primary' onClick={AddCommentComponent}>comment</Link>
                                        <Link to="/" className='btn btn-danger'>Cancel</Link>
                                    </div>
                                </form>
                            </div>
                        </div>
                </div>
            </div>
        );
    
}

export default AddCommentComponent;
